import getApi from '../@api/api.js';
import React from 'react';

class Detail extends React.Component {

    splitDate(string){
        const arr=string.split('-');
        const Y=arr[0];
        const M=arr[1];
        const D=arr[2].split('T')[0];
        let T=arr[2].split('T')[1].split('.')[0];
        const AM=Number(T.subString(0,2))<12;
        return [Y,M,D,T,AM];
    }
    componentDidMount(){
        let taken=this.splitDate(this.props.location.state.photoInfo.photoTakenAt)
        
        console.log(taken[0],taken[1],taken[2],taken[3],taken[4])
    }
    render() {
        console.log(this.props)
        return <>
            <div style={{ fontFamily: "'Noto Sans KR'", fontSize: 18, color: "#434959", marginLeft: 28, marginTop: 25 }}>파일 상세 정보</div>
            <div style={{ display: 'flex', marginLeft: 28, marginTop: 23}}>
                <div style={{ flex: 1 }}>
                    <img src={this.props.location.state.photoInfo.photoUrl} style={{width:"80%"}} />
                    <div style={{marginTop:21}}>
                        <div style={{display:"flex"}}><div style={{flex:3}}>파일명</div> <div style={{flex:8}}> {this.props.location.state.name}</div></div>
                        <div style={{display:"flex"}}><div style={{flex:3}}>촬영시간</div> <div style={{flex:8}}> {this.props.location.state.photoInfo.photoTakenAt}</div></div>
                        <div style={{display:"flex"}}><div style={{flex:3}}>등록일</div> <div style={{flex:8}}> {this.props.location.state.photoInfo.createdAt}</div></div>
                    </div>
                </div>
                <div style={{ flex: 3 }}>
                    <img src={this.props.location.state.photoInfo.photoUrl} />
                </div>

            </div>
        </>
    }
}

export default Detail;
