import getApi from '../@api/api.js';
import React from 'react';
import Pagination from "react-js-pagination";
import { Link } from 'react-router-dom';


class Photo extends React.Component{
  state={
    img:"",
    name:"",
    labelCnt:0,
    complete:false
  }
  handleSuccess = () => {
    this.props.history.push('/detail');
  };
  render(){
    return <Link to={{pathname:'/detail'}} style={{flex:1,flexBasis:"25%",textAlign:"center"}}>
      <img src={this.props.photoUrl} style={{height:"80%",width:"80%"}} />
      </Link>
  }
}

class PhotoList extends React.Component {
  state={
    photos:[],
    loaded:false,
    labels:[],
    chklist:[],
    page:1,
    maxPage:1,
    meta:""
  };
  async componentDidMount() {
    const data = await getApi.getPhotoList(this.state.page,12,this.state.chklist);
    const meta = data.meta;
    const photos =data.photos;
    const labels = await getApi.getLabelList();
    this.setState({photos:photos,labels:labels, loaded:true, maxPage:meta.maxPage,meta:meta});
  }
  async componentDidUpdate(prevProps, prevState){
    if(prevState.page!== this.state.page){
      const data = await getApi.getPhotoList(this.state.page,12,this.state.chklist);
      const meta = data.meta;
      const photos =data.photos;
      this.setState({photos:photos, loaded:true, maxPage:meta.maxPage});
    }
  }
  setIndex = sendPage => {
    this.setState({
      page: sendPage,
    });
  };
  handleLabelClick = (e)=>{
    console.log(e.target.id);
    let arr=this.state.chklist;
    if(e.target.checked){
      arr.push(e.target.id);
      this.setState({chklist:arr},async ()=>{
        const photos = await getApi.getPhotoList(1,12,this.state.chklist);
        this.setState({photos:photos});
        console.log(this.state.chklist);
      });
    }
    else{
      arr=arr.filter((element)=>element!==e.target.id);
      this.setState({chklist:arr},async ()=>{
        const photos = await getApi.getPhotoList(1,12,this.state.chklist);
        this.setState({photos:photos});
        console.log(this.state.chklist)}
      );
    }
  }
  handlePageChange(pageNumber){
    console.log(pageNumber)
    this.setState({page: pageNumber});
  }

  render() {
    if(!this.state.loaded) return <></>;
    else return <>
      {this.state.labels.map((item)=>(
        <label><input type="checkbox" id={item.id} onChange={(e)=>this.handleLabelClick(e)} />{item.title}</label>
      ))}

      <div style={{display:'flex',flexWrap:"wrap",justifyContent:"space-evenly"}}>
      {this.state.photos.map((item)=>(
        <Photo photoUrl={item.photoUrl}  history={this.props.history}/>
      ))}
      </div>
      <Pagination
          activePage={this.state.page}
          itemsCountPerPage={this.state.meta.per}
          totalItemsCount={this.state.meta.total}
          pageRangeDisplayed={8}
          onChange={this.handlePageChange.bind(this)}
      />
    </>
  }
}

export default PhotoList;
