import getApi from '../@api/api.js';
import React from 'react';
import Pagination from "react-js-pagination";
import { Link } from 'react-router-dom';
import './paging.css';


class Photo extends React.Component {
  state = {
    img: "",
    name: "",
    labelCnt: 0,
    complete: false
  }
  componentDidMount(){
    let parse=this.props.photoInfo.photoUrl.split('/');
    const name=parse[parse.length-1];
    const labelCnt=this.props.photoInfo.labels.length;
    this.setState({name:name,labelCnt:labelCnt});
  }
  render() {
    return <div style={{ width: "23vw", height: "24vw", textAlign: "center", margin: 6, border: "1px solid #dfe3ea" }}>
      <Link to={{ pathname: '/detail', state: { photoInfo: this.props.photoInfo } }} >
        <img src={this.props.photoInfo.photoUrl} style={{ height: "77%", width: "100%",position:"relative" }} />
      </Link>
      <div style={{position:"absolute",top:10}}>안뇽</div>
      <div style={{display:"flex",justifyContent:"space-between",alignItems:"center",height:"23%",marginRight:10,marginLeft:10}}>
       <div stlye={{fontFamily: "'Noto Sans KR'", fontSize: 13, color: "#434959"}}>{this.state.name}</div>
       <div stlye={{fontFamily: "'Noto Sans KR'", fontSize: 13, color: "#434959"}}>라벨 {this.state.labelCnt}개</div>
      </div>
    </div>
  }
}


class PhotoList extends React.Component {
  state = {
    photos: [],
    loaded: false,
    page: 1,
    meta: ""
  };
  async componentDidMount() {
    const data = await getApi.getPhotoList(this.props.page, 12, this.props.chklist);
    const meta = data.meta;
    const photos = data.photos;

    this.setState({ photos: photos, loaded: true, meta: meta });

  }
  async componentDidUpdate(prevProps, prevState) {
    console.log(this.props.chklist, prevProps.chklist);
    if (prevProps.chklist !== this.props.chklist || prevProps.page !== this.props.page) {
      const data = await getApi.getPhotoList(this.props.page, 12, this.props.chklist);
      const meta = data.meta;
      const photos = data.photos;
      this.setState({ photos: photos, loaded: true });
    }
  }
  setIndex = sendPage => {
    this.setState({
      page: sendPage,
    });
  };
  handleLabelClick = (e) => {
    console.log(this.props.chklist, e.target.id, this.props.chklist.includes(e.target.id));
    let arr = this.props.chklist;
    if (e.target.checked) {
      arr.push(e.target.id);
      console.log(this.props.chklist, arr);
      this.props.setChklist(arr)
      const data = getApi.getPhotoList(this.props.page, 12, this.props.chklist);

      this.setState({ photos: data.photos });
      console.log(this.props.chklist, e.target.id, this.props.chklist.includes(e.target.id));
      this.props.setPage(1);
      //this.setState({chklist:arr});
    }
    else if (!e.target.checked) {
      arr = arr.filter((element) => element !== e.target.id);
      this.props.setChklist(arr);
      this.props.setPage(1);
      const data = getApi.getPhotoList(this.props.page, 12, this.props.chklist);
      this.setState({ photos: data.photos });
      console.log(this.props.chklist)
    }

  }

  handlePageChange(pageNumber) {
    console.log(pageNumber)
    this.props.setPage(pageNumber);
  }

  render() {
    console.log("render!");
    if (!this.state.loaded) return <></>;
    else return <>
      <div style={{ fontSize: 16, fontFamily: "'Noto Sans KR'", marginTop: 27, marginLeft: 50 }}>라벨 종류 선택</div>
      <div style={{ marginTop: 12, marginLeft: 28, marginRight: 28, paddingBottom: 25, borderBottom: "1px solid #dfe3ea" }}>
        {this.props.labels.map((item) => (
          <label style={{ fontFamily: "'Noto Sans KR'", fontSize: 13, color: "#727682", marginRight: 8 }}><input type="checkbox" id={item.id} onChange={(e) => this.handleLabelClick(e)} checked={this.props.chklist.includes(String(item.id))} />{item.title}</label>
        ))}
      </div>
      <div style={{ fontFamily: "'Noto Sans KR'", fontSize: 16, color: "##434959", marginLeft: 28,marginTop:24 }}>
        전체 {this.state.meta.total.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}개
      </div>
      <div style={{ display: 'flex', flexWrap: "wrap", justifyContent: "space-between", marginLeft:28,marginRight:28 }}>
        {this.state.photos.map((item) => (
          <Photo photoInfo={item} />
        ))}
      </div>
      <Pagination
        itemClass="page-item"
        hideFirstLastPages={true}
        activePage={this.props.page}
        itemsCountPerPage={this.state.meta.per}
        totalItemsCount={this.state.meta.total}
        pageRangeDisplayed={8}
        onChange={this.handlePageChange.bind(this)}
      />
    </>
  }
}

export default PhotoList;
