import getApi from './@api/api';
import React from 'react';

class App extends React.Component {
  state={
    photos:[],
    loaded:false,
    labels:[],
    chklist:[],
  };
  async componentDidMount() {
    const photos = await getApi.getPhotoList(1,12,this.state.chklist);
    const labels = await getApi.getLabelList();
    this.setState({photos:photos,labels:labels, loaded:true});
    console.log(this.state.chklist);
  }

  handleLabelClick = async (e)=>{
    if(!e.target.checked){
      console.log(this.state.chklist);
      this.setState({chklist:[e.target.id]});
      console.log(this.state.chklist);
      const photos = await getApi.getPhotoList(1,12,this.state.chklist);
      this.setState({photos:photos});
    }
  }
  render() {
    if(!this.state.loaded) return <></>;
    else return <>
      {this.state.labels.map((item,i)=>(
        <label><input type="checkbox" checked={this.chklist.includes(1)} id={item.id} onChange={(e)=>this.handleLabelClick(e)} />{item.title}</label>
      ))}
      {this.state.photos.map((item)=>(
        <div>
          <img src={item.photoUrl}/>
        </div>
      ))}
    </>
  }
}


export default App;
