import getApi from '../@api/api.js';
import React from 'react';

class Detail extends React.Component {
    state = {
        takenAt: "",
        createdAt: ""
    }
    splitDate(string) {
        const arr = string.split('-');
        const Y = arr[0];
        const M = arr[1].replace(/(^0+)/, "");;
        const D = arr[2].split('T')[0].replace(/(^0+)/, "");
        const T = arr[2].split('T')[1].split('.')[0];
        const AM = Number(T.substring(0, 2)) < 12;
        return [Y, M, D, T, AM];
    }
    componentDidMount() {
        let taken = this.splitDate(this.props.location.state.photoInfo.photoTakenAt)
        let create = this.splitDate(this.props.location.state.photoInfo.createdAt)
        let takenStr = ''
        if (taken[4]) takenStr = taken[0] + '. ' + taken[1] + '. ' + taken[2] + '. 오전 ' + taken[3]
        else takenStr = taken[0] + '. ' + taken[1] + '. ' + taken[2] + '. 오후 ' + taken[3]
        let createStr = create[0] + '. ' + create[1] + '. ' + create[2] + '.'
        this.setState({ takenAt: takenStr, createdAt: createStr })
    }
    render() {
        console.log(this.props.location.state.labels)
        return <>
            <div style={{ fontFamily: "'Noto Sans KR'", fontSize: 18, color: "#434959", marginLeft: 28, marginTop: 25 }}>파일 상세 정보</div>
            <div style={{ display: 'flex', marginLeft: 28, marginTop: 23 }}>
                <div style={{ flex: 1 }}>
                    <img src={this.props.location.state.photoInfo.photoUrl} style={{ width: "90%",boxShadow:"0 2px 2px 0 #ecedef", border:"solid 1px #dfe3ea",}} />
                    <div style={{ marginTop: 21 }}>
                        <div style={{ display: "flex",fontFamily: "'Noto Sans KR'", fontSize: 13 }}><div style={{ flex: 3 }}>파일명</div> <div style={{ flex: 8 }}> {this.props.location.state.name}</div></div>
                        <div style={{ display: "flex",fontFamily: "'Noto Sans KR'", fontSize: 13,marginTop:8 }}><div style={{ flex: 3 }}>촬영시간</div> <div style={{ flex: 8 }}> {this.state.takenAt}</div></div>
                        <div style={{ display: "flex" ,fontFamily: "'Noto Sans KR'", fontSize: 13,marginTop:8}}><div style={{ flex: 3 }}>등록일</div> <div style={{ flex: 8 }}> {this.state.createdAt}</div></div>
                    </div>
                </div>
                <div style={{ flex: 3,marginRight:28 }}>
                    <div style={{fontFamily: "'Noto Sans KR'", fontSize: 14, color: "#727682"}}>라벨 정보</div>
                    {this.props.location.state.photoInfo.labels.map((item, index) => (
                        <div style={{marginTop:11,borderRadius: 10, boxShadow:"0 2px 2px 0 #ecedef", border:"solid 1px #dfe3ea",  padding: "16px 16px 17px 20px"}}>
                            <div  style={{fontFamily: "'Noto Sans KR'", fontSize: 16, color: "#434959"}}>라벨 #{item.typeId}</div>
                            <div style={{ display: "flex" ,fontFamily: "'Noto Sans KR'", fontSize: 16, marginTop:10}}><div style={{ flex: 1, color: "#434959" }}>유형</div> <div style={{ flex: 8,color:"#727682" }}> {this.props.location.state.labels.find(i=>i.id==item.typeId)['title']}</div></div>
                        <div style={{ display: "flex",fontFamily: "'Noto Sans KR'", fontSize: 16, marginTop:8}}><div style={{ flex: 1, color: "#434959" }}>설명</div> <div style={{ flex: 8,color:"#727682" }}> {item.description}</div></div>
                        </div>
                    ))}
                </div>

            </div>
        </>
    }
}

export default Detail;
