import getApi from '../@api/api.js';
import React from 'react';

class PhotoList extends React.Component {
  state={
    photos:[],
    loaded:false,
    labels:[],
    chklist:[],
  };
  async componentDidMount() {
    const photos = await getApi.getPhotoList(1,12,this.state.chklist);
    const labels = await getApi.getLabelList();
    this.setState({photos:photos,labels:labels, loaded:true});
  }

  handleLabelClick = (e)=>{
    console.log(e.target.id);
    let arr=this.state.chklist;
    if(e.target.checked){
      arr.push(e.target.id);
      this.setState({chklist:arr},async ()=>{
        const photos = await getApi.getPhotoList(1,12,this.state.chklist);
        this.setState({photos:photos});
        console.log(this.state.chklist);
      });
    }
    else{
      arr=arr.filter((element)=>element!==e.target.id);
      this.setState({chklist:arr},async ()=>{
        const photos = await getApi.getPhotoList(1,12,this.state.chklist);
        this.setState({photos:photos});
        console.log(this.state.chklist)}
      );
    }
  }
  render() {
    if(!this.state.loaded) return <></>;
    else return <>

      {this.state.labels.map((item)=>(
        <label><input type="checkbox" id={item.id} onChange={(e)=>this.handleLabelClick(e)} />{item.title}</label>
      ))}

      <div style={{display:'flex',flexWrap:"wrap",justifyContent:"space-evenly"}}>
      {this.state.photos.map((item)=>(
        <Photo img={item.photoUrl} name/>
      ))}
      </div>
    </>
  }
}

class Photo extends React.Component{
  state={
    img:"",
    name:"",
    labelCnt:0,
    complete:false
  }
  render(){
    return <div style={{flex:1,flexBasis:"25%",textAlign:"center"}}>
      <img src={this.props.photoUrl} style={{height:"80%",width:"80%"}}/>
      </div>
  }
}


export default PhotoList;
